let object_name=document.getElementById("preform_form_name");object_name.addEventListener("change",function(){let replaceId="preform_form_parent";let replaceElement=document.getElementById(replaceId+"_select2");replaceElement.classList.add("disabled");const requestModalName=new XMLHttpRequest;requestModalName.responseType="document";let preformForm=document.forms.preform_form;let formData=new FormData;formData.append(this.getAttribute("name"),this.value);requestModalName.open(preformForm.getAttribute("method"),preformForm.getAttribute("action"),true);requestModalName.setRequestHeader("X-Requested-With","XMLHttpRequest");requestModalName.addEventListener("readystatechange",function(){if(requestModalName.readyState===4&&requestModalName.status===200){let result=requestModalName.response.getElementById(replaceId);let select2=document.getElementById(replaceId+"_select2");if(select2){select2.remove()}document.getElementById(replaceId).replaceWith(result);new NiceSelect(document.getElementById(replaceId),{searchable:true,id:"select2-"+replaceId})}return false});requestModalName.send(formData)});